import { storage } from "../storage";
import { openaiService } from "./openaiService";
import { createRequire } from 'module';

const require = createRequire(import.meta.url);

export interface ParsedDocument {
  title: string;
  filename: string;
  content: string;
}

export class DocumentService {
  async parseFile(file: Express.Multer.File): Promise<ParsedDocument> {
    const { originalname, buffer, mimetype } = file;
    let content = '';

    console.log(`Processing file: ${originalname}, type: ${mimetype}, size: ${buffer.length} bytes`);

    try {
      if (mimetype === 'application/pdf') {
        try {
          const pdfParse = require('pdf-parse');
          const data = await pdfParse(buffer);
          content = data.text;
          console.log(`Successfully parsed PDF, extracted ${content.length} characters`);
        } catch (pdfError) {
          console.error('PDF parsing error:', pdfError);
          throw new Error(`PDF parsing failed: ${(pdfError as any).message}`);
        }
      } else if (mimetype === 'application/vnd.openxmlformats-officedocument.wordprocessingml.document') {
        try {
          const mammoth = await import('mammoth');
          const result = await mammoth.extractRawText({ buffer });
          content = result.value;
          console.log(`Successfully parsed DOCX, extracted ${content.length} characters`);
        } catch (docxError) {
          console.error('DOCX parsing error:', docxError);
          throw new Error(`DOCX parsing failed: ${(docxError as any).message}`);
        }
      } else if (mimetype === 'text/plain') {
        content = buffer.toString('utf-8');
      } else {
        throw new Error(`Unsupported file type: ${mimetype}`);
      }

      return {
        title: originalname.replace(/\.[^/.]+$/, ""), // Remove file extension
        filename: originalname,
        content: content.trim(),
      };
    } catch (error) {
      throw new Error(`Failed to parse file: ${(error as any).message}`);
    }
  }

  chunkText(text: string, chunkSize: number = 1000, overlap: number = 200): string[] {
    const chunks: string[] = [];
    let start = 0;

    while (start < text.length) {
      const end = Math.min(start + chunkSize, text.length);
      const chunk = text.slice(start, end);
      chunks.push(chunk.trim());

      if (end >= text.length) break;
      start = end - overlap;
    }

    return chunks.filter(chunk => chunk.length > 0);
  }

  async processDocument(file: Express.Multer.File): Promise<{ documentId: number; chunkCount: number }> {
    // Parse the file
    const parsedDoc = await this.parseFile(file);

    // Create document in database
    const document = await storage.createDocument(parsedDoc);

    // Chunk the text
    const chunks = this.chunkText(parsedDoc.content);

    // Generate embeddings and store chunks
    let chunkCount = 0;
    for (const chunkText of chunks) {
      try {
        const embedding = await openaiService.createEmbedding(chunkText);
        await storage.createChunk({
          documentId: document.id,
          chunkText,
          embedding: `[${embedding.join(',')}]`, // Convert array to vector format
        });
        chunkCount++;
      } catch (error) {
        console.error(`Failed to process chunk: ${(error as any).message}`);
        // Continue processing other chunks
      }
    }

    return {
      documentId: document.id,
      chunkCount,
    };
  }

  async deleteDocument(id: number): Promise<void> {
    // First delete all chunks associated with the document
    await storage.deleteChunksByDocumentId(id);

    // Then delete the document
    await storage.deleteDocument(id);
  }
}

export const documentService = new DocumentService();

server/services/openaiService.ts.content:
import OpenAI from "openai";

// the newest OpenAI model is "gpt-4o" which was released May 13, 2024. do not change this unless explicitly requested by the user
const openai = new OpenAI({
  apiKey: process.env.OPENAI_API_KEY || process.env.OPENAI_API_KEY_ENV_VAR || "sk-default",
});

export class OpenAIService {
  async createEmbedding(text: string): Promise<number[]> {
    try {
      const response = await openai.embeddings.create({
        model: "text-embedding-ada-002",
        input: text,
      });

      return response.data[0].embedding;
    } catch (error) {
      throw new Error(`Failed to create embedding: ${(error as any).message}`);
    }
  }

  async generateChatResponse(messages: any[], systemPrompt?: string): Promise<string> {
    try {
      const chatMessages: any[] = [];

      if (systemPrompt) {
        chatMessages.push({ role: "system", content: systemPrompt });
      }

      chatMessages.push(...messages);

      const response = await openai.chat.completions.create({
        model: "gpt-4o", // the newest OpenAI model is "gpt-4o" which was released May 13, 2024. do not change this unless explicitly requested by the user
        messages: chatMessages,
        max_tokens: 500,
        temperature: 0.7,
      });

      return response.choices[0].message.content || "I apologize, but I couldn't generate a response.";
    } catch (error) {
      throw new Error(`Failed to generate chat response: ${(error as any).message}`);
    }
  }

  async searchSimilarChunks(query: string, chunks: { chunkText: string; embedding: string }[]): Promise<string[]> {
    try {
      // Generate embedding for the query
      const queryEmbedding = await this.createEmbedding(query);

      // Calculate similarity scores (simplified cosine similarity)
      const similarities = chunks.map(chunk => {
        const embedding = JSON.parse(chunk.embedding);
        const similarity = this.cosineSimilarity(queryEmbedding, embedding);
        return { text: chunk.chunkText, similarity };
      });

      // Sort by similarity and return top 3 chunks
      return similarities
        .sort((a, b) => b.similarity - a.similarity)
        .slice(0, 3)
        .map(item => item.text);
    } catch (error) {
      console.error('Failed to search similar chunks:', error);
      return [];
    }
  }

  private cosineSimilarity(a: number[], b: number[]): number {
    const dotProduct = a.reduce((sum, ai, i) => sum + ai * b[i], 0);
    const magnitudeA = Math.sqrt(a.reduce((sum, ai) => sum + ai * ai, 0));
    const magnitudeB = Math.sqrt(b.reduce((sum, bi) => sum + bi * bi, 0));

    if (magnitudeA === 0 || magnitudeB === 0) return 0;
    return dotProduct / (magnitudeA * magnitudeB);
  }
}

export const openaiService = new OpenAIService();

