                </CardContent>
              </Card>

              {/* Save Settings */}
              <div className="flex justify-end">
                <Button 
                  onClick={handleSaveSettings}
                  className="bg-blue-500 text-white px-6 py-2 rounded-lg hover:bg-blue-600 transition-colors"
                >
                  Save Settings
                </Button>
              </div>
            </div>
          </div>
        </main>
      </div>
    </div>
  );
}

