import { Card, CardContent } from "@/components/ui/card";
import { 
  LineChart, 
  Line, 
  BarChart, 
  Bar, 
  XAxis, 
  YAxis, 
  CartesianGrid, 
  ResponsiveContainer,
  PieChart,
  Pie,
  Cell
} from "recharts";

interface AnalyticsChartsProps {
  data: {
    dailyMessages: Array<{ date: string; count: number }>;
  };
  loading: boolean;
}

const COLORS = ['#10b981', '#3b82f6', '#f59e0b', '#ef4444'];

export default function AnalyticsCharts({ data, loading }: AnalyticsChartsProps) {
  if (loading) {
    return (
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mb-8">
        <Card className="bg-white shadow-sm border-slate-200">
          <CardContent className="p-6">
            <h3 className="text-lg font-semibold text-slate-900 mb-4">Daily Message Volume</h3>
            <div className="h-64 flex items-center justify-center text-slate-500">
              Loading chart...
            </div>
          </CardContent>
        </Card>

        <Card className="bg-white shadow-sm border-slate-200">
          <CardContent className="p-6">
            <h3 className="text-lg font-semibold text-slate-900 mb-4">Response Time Distribution</h3>
            <div className="h-64 flex items-center justify-center text-slate-500">
              Loading chart...
            </div>
          </CardContent>
        </Card>
      </div>
    );
  }

  // Transform daily messages data for charts
  const chartData = data?.dailyMessages?.map((item) => ({
    day: new Date(item.date).toLocaleDateString('en-US', { weekday: 'short' }),
    messages: item.count,
  })) || [];

  // Mock response time data (in a real app, this would come from the API)
  const responseTimeData = [
    { name: '< 1s', value: 45, count: 450 },
    { name: '1-2s', value: 35, count: 350 },
    { name: '2-3s', value: 15, count: 150 },
    { name: '> 3s', value: 5, count: 50 },
  ];

  // Generate monthly data for bar chart
  const monthlyData = [
    { month: 'Jan', messages: 850 },
    { month: 'Feb', messages: 920 },
    { month: 'Mar', messages: 780 },
    { month: 'Apr', messages: 1100 },
    { month: 'May', messages: 1250 },
    { month: 'Jun', messages: 1400 },
    { month: 'Jul', messages: 1200 },
  ];

  return (
    <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mb-8">
      <Card className="bg-white shadow-sm border-slate-200">
        <CardContent className="p-6">
          <h3 className="text-lg font-semibold text-slate-900 mb-4">Daily Message Volume</h3>
          <div className="h-64">
            <ResponsiveContainer width="100%" height="100%">
              {chartData.length > 0 ? (
                <LineChart data={chartData}>
                  <CartesianGrid strokeDasharray="3 3" stroke="#e2e8f0" />
                  <XAxis dataKey="day" stroke="#64748b" />
                  <YAxis stroke="#64748b" />
                  <Line 
                    type="monotone" 
                    dataKey="messages" 
                    stroke="#3b82f6" 
                    strokeWidth={2}
                    dot={{ fill: '#3b82f6', strokeWidth: 2, r: 4 }}
                  />
                </LineChart>
              ) : (
                <BarChart data={monthlyData}>
                  <CartesianGrid strokeDasharray="3 3" stroke="#e2e8f0" />
                  <XAxis dataKey="month" stroke="#64748b" />
                  <YAxis stroke="#64748b" />
                  <Bar dataKey="messages" fill="#3b82f6" radius={[4, 4, 0, 0]} />
                </BarChart>
              )}
            </ResponsiveContainer>
          </div>
        </CardContent>
      </Card>

      <Card className="bg-white shadow-sm border-slate-200">
        <CardContent className="p-6">
          <h3 className="text-lg font-semibold text-slate-900 mb-4">Response Time Distribution</h3>
          <div className="h-64">
            <ResponsiveContainer width="100%" height="100%">
              <PieChart>
                <Pie
                  data={responseTimeData}
                  cx="50%"
                  cy="50%"
                  innerRadius={60}
                  outerRadius={100}
                  paddingAngle={5}
                  dataKey="value"
                >
                  {responseTimeData.map((entry, index) => (
                    <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                  ))}
                </Pie>
              </PieChart>
            </ResponsiveContainer>
          </div>
          <div className="mt-4 grid grid-cols-2 gap-2 text-sm">
            {responseTimeData.map((entry, index) => (
              <div key={entry.name} className="flex items-center">
                <div 
                  className="w-3 h-3 rounded-full mr-2" 
                  style={{ backgroundColor: COLORS[index % COLORS.length] }}
                />
                <span className="text-slate-600">{entry.name}: {entry.value}%</span>
              </div>
            ))}
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/chat/chat-interfac.content:
import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { ScrollArea } from "@/components/ui/scroll-area";
import { User, X } from "lucide-react";

interface ChatInterfaceProps {
  sessionId: string;
}

interface Message {
  id: number;
  message: {
    type: string;
    content: string;
    timestamp: string;
  };
}

export default function ChatInterface({ sessionId }: ChatInterfaceProps) {
  const [showChat, setShowChat] = useState(true);

  const { data: messages, isLoading } = useQuery({
    queryKey: ["/api/messages", { session_id: sessionId }],
    enabled: !!sessionId,
  });

  const formatTime = (timestamp: string) => {
    return new Date(timestamp).toLocaleTimeString('en-US', {
      hour: 'numeric',
      minute: '2-digit',
      hour12: true,
    });
  };

  if (!showChat) {
    return null;
  }

  return (
    <div className="border border-slate-200 rounded-lg h-96 flex flex-col">
      <div className="bg-slate-50 px-4 py-3 border-b border-slate-200 rounded-t-lg">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-3">
            <div className="w-8 h-8 bg-slate-300 rounded-full flex items-center justify-center">
              <User className="w-4 h-4 text-slate-600" />
            </div>
            <div>
              <p className="font-medium text-slate-900">{sessionId}</p>
              <p className="text-sm text-slate-500">WhatsApp Session</p>
            </div>
          </div>
          <Button 
            variant="ghost" 
            size="sm"
            onClick={() => setShowChat(false)} 
            className="text-slate-400 hover:text-slate-600"
          >
            <X className="w-5 h-5" />
          </Button>
        </div>
      </div>

      <ScrollArea className="flex-1 p-4 bg-slate-50">
        {isLoading ? (
          <div className="text-center text-slate-500 py-8">Loading messages...</div>
        ) : Array.isArray(messages) && messages.length > 0 ? (
          <div className="space-y-4">
            {messages.map((msg: Message) => {
              const isUser = msg.message.type === 'user';

              return (
                <div key={msg.id} className={`flex ${isUser ? '' : 'justify-end'}`}>
                  <div className={`max-w-xs lg:max-w-md px-4 py-2 rounded-lg shadow-sm ${
                    isUser 
                      ? 'bg-white border border-slate-200' 
                      : 'bg-blue-500 text-white'
                  }`}>
                    <p className="text-sm">{msg.message.content}</p>
                    <p className={`text-xs mt-1 ${
                      isUser ? 'text-slate-500' : 'text-blue-200'
                    }`}>
                      {msg.message.timestamp ? formatTime(msg.message.timestamp) : 'Just now'}
                    </p>
                  </div>
                </div>
              );
            })}
          </div>
        ) : (
          <div className="text-center text-slate-500 py-8">
            No messages in this session yet.
          </div>
        )}
      </ScrollArea>
    </div>
  );
}

